                  {errors.industry.message}

              )}


            {watchIndustry && (


                Specialization
                 setValue("subIndustry", value)}
                >





                      Specializations
                      {selectedIndustry?.subIndustries.map((sub) => (

                          {sub}

                      ))}



                {errors.subIndustry && (


                    {errors.subIndustry.message}

                )}

            )}



              Years of Experience

